SELECT BId, BType, BName, BMonth, BPrice,  BUpdateTime, BMark
FROM bill
WHERE BId = #{bId}

     SELECT BId, BType, BName, BMonth, BPrice, BUpdateTime, BMark
FROM bill
WHERE BMark=#{bMark}

     SELECT BId, BType, BName, BMonth, BPrice, BUpdateTime, BMark
FROM bill
WHERE BName=#{bName}
