mapper.update:
UPDATE staff SET SPassword = #{newPassword},
SSalt=#{sSalt}
WHERE SId = #{sId}

mapper.select:
SELECT SId, SName, SPassword, SSalt, SEmail, SPhone, SSex, SBirthday, SEnter, SMark, SAddress, SAvatar
FROM staff
WHERE SEmail= #{sEmail}

SELECT SId, SName, SPassword, SSalt, SEmail, SPhone, SSex, SBirthday, SEnter, SMark, SAddress,SAvatar
FROM staff
WHERE SPhone = #{sPhone}
